    <script>
        document.addEventListener("DOMContentLoaded", function() {
            const confirmButton = document.getElementById("confirmButton");
            const modalContents = [
                document.getElementById("modalContent1"),
                document.getElementById("modalContent2"),
                document.getElementById("modalContent3")
            ];

            confirmButton.addEventListener("click", function() {
                const selectedRadio = document.querySelector('input[name="inlineRadioOptions"]:checked');

                if (selectedRadio) {
                    const selectedValue = selectedRadio.value;
                    const modalBody = document.querySelector(".modal-body");

                    // Hide all modal contents
                    modalContents.forEach(content => content.classList.add("d-none"));

                    // Show the appropriate modal content based on selected value
                    if (selectedValue === "option1") {
                        modalContents[0].classList.remove("d-none");
                    } else if (selectedValue === "option2") {
                        modalContents[1].classList.remove("d-none");
                    } else if (selectedValue === "option3") {
                        modalContents[2].classList.remove("d-none");
                    }

                    // Open the modal
                    new bootstrap.Modal(document.getElementById("testModal")).show();
                }
            });
        });
    </script>
